import java.util.Scanner; public class PRB11 { public static void main(String[] Args){Scanner in = new Scanner(System.in);int length = in.nextInt(); in.nextLine();char[] key = in.nextLine().replaceAll(" ", "").toCharArray();char[] answers = in.nextLine().replaceAll(" ", "").toCharArray();int correct = 0;for(int i = 0; i < key.length; i++)if(key[i] == answers[i]) correct++;System.out.println((correct * 4) - (length-correct));in.close();}}